import React, { useState, useEffect } from 'react';
import { Carousel } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { getImagesByHotelId, getHotelsC } from './api';

function Landing() {
  const [hoteles, setHoteles] = useState([]);

  const getHoteles = async () => {
    try {
      const response = await getHotelsC();
      const hotelsData = response.data.hotels;

      // Mapear sobre la lista de hoteles y obtener la primera imagen para cada uno
      const hotelesConImagenes = await Promise.all(
        hotelsData.map(async (hotel) => {
          try {
            // Obtener la primera imagen para el hotel actual
            const imagenResponse = await getImagesByHotelId(hotel.id);
            const primeraImagen = imagenResponse.data.images[0]?.imagen || null;

            console.log(`Hotel ${hotel.id}:`, {
              primeraImagen: primeraImagen ? 'Imagen presente' : 'Sin imagen',
            });

            // Devolver el hotel con la información de la primera imagen
            return {
              ...hotel,
              primeraImagen,
            };
          } catch (error) {
            console.error(`Error al obtener la imagen para el hotel ${hotel.id}:`, error);
            return hotel; // En caso de error, devolver el hotel sin la imagen
          }
        })
      );

      // Actualizar el estado con la nueva lista de hoteles que incluyen la primera imagen
      setHoteles(hotelesConImagenes);
    } catch (error) {
      console.error('Error al obtener hoteles:', error);
    }
  };

  useEffect(() => {
    getHoteles();
  }, []); // La dependencia vacía asegura que estos efectos solo se ejecuten una vez al montar el componente.

  return (
    <Carousel>
      {hoteles.map((hotel) => (
        <Carousel.Item key={hotel.id} value={hotel.id}>
          {hotel.primeraImagen ? (
            <img
              className="d-block w-100"
              src={`data:image/jpeg;base64,${btoa(String.fromCharCode.apply(null, hotel.primeraImagen))}`}
              alt={`Imagen de ${hotel.name}`}
            />
          ) : (
            <img
              className="d-block w-100"
              src="https://mcaleer-rushe.co.uk/site/wp-content/uploads/2019/05/Maldron-Hotel-Belfast-IntAirport-I.jpg"
              alt="Respaldo"
            />
          )}
          <Link to={`/hotel/${hotel.id}`}>
            <Carousel.Caption
              style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'flex-end',
                height: '100%',
              }}
            >
              <h1>{`Maldron ${hotel.name}`}</h1>
              <p>{hotel.description}</p>
            </Carousel.Caption>
          </Link>
        </Carousel.Item>
      ))}
    </Carousel>
  );
}

export default Landing;
