import React, { useState, useEffect } from 'react';
import {Carousel} from 'react-bootstrap';
import {Link} from 'react-router-dom';
import { getImagesByHotelId, getHotelsC } from './api';

function Landing() {
  const [hoteles, setHoteles] = useState([]);
  const [imagenPerfil, setImagenPerfil] = useState(null);

  const getHoteles = async () => {
    try {
      const response = await getHotelsC();
      const reservasData = response.data.hotels;
      setHoteles(reservasData);
    } catch (error) {
      console.error('Error al obtener hoteles:', error);
    }
  };

  const getImagen = async () => {
    try {
      const response = await getImagesByHotelId();
      console.log('Respuesta de la API de imágenes:', response.data);
      const imagen = response.data.images[0].Data;

      // Elimina la parte inicial del formato
      const base64Data = imagen.replace(/^data:image\/jpeg;base64,/, '');

      // Almacenar la imagen decodificada como un objeto Uint8Array
      setImagenPerfil(new Uint8Array(atob(base64Data).split('').map(char => char.charCodeAt(0))));
    } catch (error) {
      console.error('Error al obtener la imagen de perfil:', error);
    }
  };

  useEffect(() => {
    getHoteles();
    getImagen();
  }, []);


  return (
    <Carousel>
      {hoteles.map((hotel) => (
        <Carousel.Item key={hotel.id} value={hotel.id}>
          {hotel.primeraImagen ? (
            <img
              className="d-block w-100"
              src={`data:image/jpeg;base64,${btoa(
                String.fromCharCode.apply(null, hotel.primeraImagen)
              )}`}
              alt={`Imagen de ${hotel.name}`}
            />
          ) : (
            <img
              className="d-block w-100"
              src="https://mcaleer-rushe.co.uk/site/wp-content/uploads/2019/05/Maldron-Hotel-Belfast-IntAirport-I.jpg"
              alt="Respaldo"
            />
          )}
          <Link to={`/hotel/${hotel.id}`}>
            <Carousel.Caption
              style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'flex-end',
                height: '100%',
              }}
            >
              <h1>{`Maldron ${hotel.name}`}</h1>
              <p>{hotel.description}</p>
            </Carousel.Caption>
          </Link>
        </Carousel.Item>
      ))}
    </Carousel>
  );
};

export default Landing;
