import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button ,Carousel} from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { getHotelById, getImagesByHotelIdMap } from './api';

function HotelDetail() {
  const { id, startDate, finalDate, idHotel } = useParams();
  const [hotel, setHotel] = useState();
  const [imagenes, setImagenes] = useState([]);
  const [showReservations, setShowReservations] = useState(false);

  const getHotel = async () => {
    try {
      const response = await getHotelById(id);
      setHotel(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const getImagenes = async () => {
    try {
      const response2 = await getImagesByHotelIdMap(id);
      const imgData = response2.map(image =>
        new Uint8Array(atob(image.Data).split('').map(char => char.charCodeAt(0)))
      );
      setImagenes(imgData);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    getHotel();
    getImagenes();
  }, [id]);

  useEffect(() => {
    if (startDate !== undefined && finalDate !== undefined && idHotel !== undefined) {

      console.log(hotel)
      setShowReservations(true);
    } else {
      setShowReservations(false);
    }
  }, [startDate, finalDate, idHotel,hotel]);

  return (
    <Container className="mt-5">
      <Row>
        <Col md={6}>
          {hotel ? (
            <>
              <h1>{hotel.name}</h1>
              <p>{hotel.description}</p>
            </>
          ) : (
            <>
              <h1>Cargando...</h1>
              <p>Cargando...</p>
            </>
          )}
        </Col>
        <Col md={6}>
          {showReservations && (
            <div>
              {/* Aqu√≠ coloca el contenido de la reserva */}
              <h3>Reserva</h3>
              <p>Fecha de inicio: {startDate}</p>
              <p>Fecha final: {finalDate}</p>
              <p>ID del hotel: {idHotel}</p>
            </div>
          )}
          <Carousel style={{ height: '100%', width: '100%' }}>
            {imagenes.map((imagen, index) => (
              <Carousel.Item key={index} style={{ height: '100%' }}>
                <img
                  src={`data:image/jpeg;base64,${btoa(String.fromCharCode.apply(null, imagen))}`}
                  alt={`Imagen ${index}`}
                  className="d-block w-100"
                  style={{ objectFit: 'cover', height: '100%', maxHeight: '250px' }}
                />
              </Carousel.Item>
            ))}
          </Carousel>
        </Col>
      </Row>
    </Container>
  );
}

export default HotelDetail;


