import React, { useState, useEffect } from 'react';
import { Container, Card, Carousel, Button, Row, Col } from 'react-bootstrap';
import { FaWifi, FaSwimmingPool, FaParking, FaDumbbell, FaSpa } from 'react-icons/fa';
import { getHotelById, getImagesByHotelId } from './api';
import { useParams } from 'react-router-dom';

function HotelDetail() {
    const { id } = useParams();
    const [hotel, setHotel] = useState();
    const [imagenes, setImagenes] = useState([]);

    const GetHotelById = async () =>{
      try{
        const response = await getHotelById(id);
        const hotelData = response.data

        setHotel(hotelData)
        console.log(hotel)
      }catch(error){
      }
    }
    const getImagenes = async () =>{
      try{
        const response2 = await getImagesByHotelId(id);
        const imgData = response.data
        setImagenes(imgData)
      }catch(error){
      }
    }

    useEffect(() => {
        GetHotelById();
        getImagenes();
    }, [id]);


    useEffect(() => {
      console.log("Hotel actualizado:", hotel);

  }, [hotel]);


  return (
    <Container className="mt-5">
      <Row>
        <Col md={6}>
        {hotel ? (
          <>
            <h1>{hotel.nombre}</h1>
            <p>{hotel.descripcion}</p>
          </>
        ) : (
          <>
            <h1>Cargando...</h1>
            <p>Cargando...</p>
          </>
        )}
        </Col>
        <Col md={6}>
          <Carousel style={{ maxWidth: '100%', height: '100%' }}>
            {hotel.fotos.map((foto, index) => (
              <Carousel.Item key={index}>
                <img src={foto} alt={`Imagen ${index}`} className="d-block w-100" style={{ objectFit: 'cover', height: '100%' }} />
              </Carousel.Item>
            ))}
          </Carousel>
        </Col>
      </Row>

    </Container>
  );
}

export default HotelDetail;

