import React, { useState, useEffect } from 'react';
import {Carousel} from 'react-bootstrap';
import {Link} from 'react-router-dom';
import { getImagesByHotelId, getHotelsC } from './api';
import Reservation from './Reservation';

function Landing() {
  const [hoteles, setHoteles] = useState([]);
  const [imagenes, setImagenes] = useState([]);

  const getHoteles = async () => {
    try {
      const response = await getHotelsC();
      const hotelsData = response.data.hotels;
      let myArray = []
      for (let i=1; i<=3;i++){
        //hotelsData[i].id
        const response2 = await getImagesByHotelId(i);
        const imagesData = response2.data.images[0].Data

        myArray.push(new Uint8Array(atob(imagesData).split('').map(char => char.charCodeAt(0))))
      }
      console.log(myArray)
      setImagenes(myArray)
      setHoteles(hotelsData);
    } catch (error) {
      console.error('Error al obtener hoteles:', error);
    }
  };


  useEffect(() => {
    getHoteles();
  }, []);

  return (        
  <Carousel>
    {hoteles.map((hotel) => (
      <Carousel.Item key={hotel.id} value={hotel.id}>
        {imagenes[hotel.id-1] ? (
          <img
            className="d-block w-100"
            src={`data:image/jpeg;base64,${btoa(String.fromCharCode.apply(null, imagenes[0]))}`}
            alt={`Imagen de ${hotel.name}`}
          />
        ) : (
          <img
            className="d-block w-100"
            src="https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/23e0da0f-37e6-40e5-a0d9-da5ad7056048/d5l32dr-d6117e9f-ab65-421c-8172-202a34c72455.jpg/v1/fill/w_1024,h_498,q_75,strp/goku_and_vegeta_the_kiss_by_tracexvalintyne_d5l32dr-fullview.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..a204nMjn7GFD-WJyIyUZpyHnGXcb67GOpktYiozMUcs"
            alt="Respaldo"
          />  
        )}
        <Link to={`/hotel/${hotel.id}`}>
          <Carousel.Caption
            style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'flex-end',
              height: '100%',
            }}
          >
            <h1>{`Maldron ${hotel.name}`}</h1>
            <p>{hotel.description}</p>
          </Carousel.Caption>
        </Link>
      </Carousel.Item>
    ))}
  </Carousel>

);

}

export default Landing;
